import { Component, inject } from '@angular/core';
import { Router } from '@angular/router';
import { CalculService } from '../services/calcul.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {
  private router = inject(Router);

  title = 'Calculator';
  selector = 1;
  operator1 = '';
  operator2: number = 0;
  reset = true;
  operationSelected = false;
  operation = '';
  recentOperation = false;

  isButtonPressed = false;
  isMouseInside = false;
  result: any = null;
  errorMessage1: string | null = null;
  errorMessage: string | null = null;

  constructor(private calculService: CalculService) {}
  onButtonDown() {
    this.isButtonPressed = true;
  }
  onButtonUp() {
    this.isButtonPressed = false;
  }
  onMouseEnter() {
    this.isMouseInside = true;
  }
  onMouseLeave() {
    this.isMouseInside = false;
    if (this.isButtonPressed) {
      this.isButtonPressed = false;
    }
  }

  switch(position: number)
  {
    this.selector = position;
    document.documentElement.setAttribute("theme", position.toString());
  }


  addDigit(caracter: string)
  {
    if(this.recentOperation)
    {
      this.recentOperation = false;
      this.operator1 = '0';
    }
    if(this.operator1 === '0')
    {
      if(caracter === '0') return;
      if(caracter === '.')
        this.operator1 += caracter;
      else
        this.operator1 = caracter;
      return;
    }
    if(caracter === '.' && this.operator1.includes('.')) return;

    this.operator1 += caracter;
    this.reset = false;
  }

  Del(){
     this.errorMessage1 =null;
    this.errorMessage =null;
    this.result = null; // Réinitialise l'opérateur
    this.reset = true;
    if(this.operator1 === '0' || this.operator1 === '') return;
    if(this.operator1.length == 1 )
    {
      this.operator1 = '0';
      return;
    }
    this.operator1 = this.operator1.slice(0, this.operator1.length - 1); 
  }

  nav() {
    console.log('Navigating to login...');
    this.router.navigate(['/login']);
  }
  factoriser() {
    this.errorMessage1 =null;
    this.errorMessage =null;
    this.result = null;
    // Vérifie si le champ est vide
    if (!this.operator1 || this.operator1.trim() === '') {
      this.errorMessage1 = 'Veuillez remplir le champ avant de soumettre.';
      this.result = null; // Réinitialise le résultat
      return;
    }

    // Vérifie le format de l'expression (vous pouvez personnaliser la validation selon vos besoins)
    const isValidExpression = /^[\d\+\-\*\/\^x\s]+$/.test(this.operator1); // Accepte chiffres, opérateurs, espaces, et x

    if (!isValidExpression) {
      this.errorMessage = 'Le format est incorrect.';
      this.result = null; // Réinitialise le résultat
      return;
    }

    // Remplace les symboles pour le calcul
    const formattedExpression = this.operator1.replace(/\^/g, '**'); // Remplace ^ par **
    this.errorMessage = null; // Réinitialise le message d'erreur

    // Appelle le service pour factoriser
    this.calculService.factoriser(formattedExpression).subscribe(
      (response) => {
        if (response && response.factorisation) {
          this.result = response.factorisation; // Stocke uniquement la factorisation
          console.log('Factorisation réussie:', response.factorisation);
        } else {
          this.errorMessage = 'Réponse inattendue du serveur.';
          console.error('Réponse inattendue:', response);
        }
      },
      (error) => {
        this.errorMessage = 'Le format est incorrect.';
        console.error('Erreur lors de la factorisation:', error);
      }
    );
  }







  calculerRacines() {

    this.errorMessage1 =null;
    this.errorMessage =null;
    this.result = null;
    // Vérifie si le champ est vide
    if (!this.operator1 || this.operator1.trim() === '') {
      this.errorMessage1 = 'Veuillez remplir le champ avant de soumettre.';
      this.result = null; // Réinitialise le résultat
      return;
    }

    // Vérifie le format de l'expression (vous pouvez personnaliser la validation selon vos besoins)
    const isValidExpression = /^[\d\+\-\*\/\^x\s]+$/.test(this.operator1); // Accepte chiffres, opérateurs, espaces, et x

    if (!isValidExpression) {
      this.errorMessage = 'Le format est incorrect.';
      this.result = null; // Réinitialise le résultat
      return;
    }

    // Remplace les symboles pour le calcul
    const formattedExpression = this.operator1.replace(/\^/g, '**'); // Remplace ^ par **
    this.errorMessage = null; // Réinitialise le message d'erreur

    // Appelle le service pour racines
    this.calculService.calculerRacines(formattedExpression).subscribe(
      (response) => {
        if (response && response.racines) {
          this.result = response.racines.map((racine: string) => `x = ${racine}`).join(', '); // Stocke uniquement la racines
          console.log('racines réussie:', response.racines);
        } else {
          this.errorMessage = 'Réponse inattendue du serveur.';
          console.error('Réponse inattendue:', response);
        }
      },
      (error) => {
        this.errorMessage = 'Le format est incorrect.';
        console.error('Erreur lors de la racines:', error);
      }
    );

  }


}
