import { Component, inject } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {
  username: string = '';
  password: string = '';
  errorMessage: string = '';

  constructor(private authService: AuthService) {}

  private router = inject(Router);
  selector = 1;
  switch(position: number)
  {
    this.selector = position;
    document.documentElement.setAttribute("theme", position.toString());
  }
  inscrt(){
    this.router.navigate(['/inscription']);
}

login() {
  const credentials = { username: this.username, password: this.password };

  this.authService.login(credentials).subscribe({
    next: (response) => {
      console.log('Login réussi:', response);
      this.router.navigate(['']); // Redirige vers la page souhaitée
    },
    error: (err) => {
      console.error('Erreur de connexion:', err);
      this.errorMessage = 'Échec de la connexion. Veuillez vérifier vos identifiants.';
    },
  });
}

}
