import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject } from 'rxjs';
import { tap } from 'rxjs/operators';
import { Router } from '@angular/router';
import { jwtDecode } from 'jwt-decode';

interface AuthResponse {
  token: string;
  user: {
    id: number;
    email: string;
    nom:string;
    prenom:string
};
}

interface LoginCredentials {
  email: string;
  password: string;
}

@Injectable({
  providedIn: 'root', // Fournisseur global (disponible dans toute l'application)
})
export class AuthService {
  private apiUrl = 'http://localhost:8088/api/auth'; // Remplacez par l'URL de votre API
  private isLoggedInSubject = new BehaviorSubject<boolean>(false);
  isLoggedIn$ = this.isLoggedInSubject.asObservable();

  constructor(private http: HttpClient,private router: Router) {}

  // Méthode pour se connecter
  // Méthode pour se connecter
login(credentials: LoginCredentials): Observable<AuthResponse> {
  console.log('Tentative de connexion avec:', credentials); // Log des credentials

  return this.http.post<AuthResponse>('http://localhost:8088/api/auth/signin', credentials).pipe(
    tap({
      next: (response: AuthResponse) => {
        console.log('Réponse du serveur:', response); // Log de la réponse
        if (response && response.token) {
          localStorage.setItem('token', response.token);

          // Décodage du token pour obtenir les informations utilisateur
          const decodedToken: any = jwtDecode(response.token);
          console.log('Token décodé:', decodedToken); // Vérifier si les données sont correctes

          const userData = {
            email: decodedToken.sub, // L'email est souvent dans "sub" dans le JWT
          };

          // Appel de findByEmail pour récupérer les informations complètes
          this.findByEmail(userData.email).subscribe({
            next: (userDetails) => {
              console.log('Détails utilisateur récupérés:', userDetails);
              const fullUserData = {
                id: userDetails.id,
                email: userDetails.email,
                nom: userDetails.nom,
                prenom: userDetails.prenom,
              };

              localStorage.setItem('user', JSON.stringify(fullUserData));
              this.isLoggedInSubject.next(true);
              console.log('Utilisateur complet enregistré:', fullUserData);
            },
            error: (error) => {
              console.error('Erreur lors de la récupération des détails utilisateur:', error);
            },
          });
        }
      },
      error: (error) => {
        console.error('Erreur de connexion:', error); // Log des erreurs
      },
    })
  );
}

  findByEmail(email: string): Observable<any> {
    return this.http.get<any>(`http://localhost:8088/api/auth/findByEmail?email=${email}`);
}

  get isLoggedIn(): boolean {
    return this.isLoggedInSubject.value;
  }
  // Méthode pour s'inscrire
  register(userData: any): Observable<any> {
    return this.http.post(`${this.apiUrl}/signup`, userData);
  }
  logout() {
    // Clear the session or JWT token from localStorage or cookies
    this.isLoggedInSubject.next(false);
    localStorage.removeItem('authToken'); // Example of clearing the token
    this.router.navigate(['/login']); // Redirect to the login page after logout
  }
  signup(userData: {
    nom: string,
    prenom: string,
    email: string,
    password: string,
    role: string[]
  }): Observable<any> {
    return this.http.post(`${this.apiUrl}/signup`, userData);
  }

  getUserId(): number | null {
    // Utiliser getUserIdFromToken pour plus de cohérence
    return this.getUserIdFromToken();
  }

  getUserIdFromToken(): number | null {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const decodedToken: any = jwtDecode(token);
        return decodedToken?.id || null;
      } catch (error) {
        console.error('Invalid token:', error);
        return null;
      }
    }
    return null;
  }

}
